CREATE TABLE IF NOT EXISTS ldap_oc_mappings (	id integer unsigned not null primary key auto_increment,	name varchar(64) not null,	keytbl varchar(64) not null,	keycol varchar(64) not null,	create_proc varchar(255),	delete_proc varchar(255),	expect_return tinyint not null);
CREATE TABLE IF NOT EXISTS ldap_attr_mappings (	id integer unsigned not null primary key auto_increment,	oc_map_id integer unsigned not null references ldap_oc_mappings(id),	name varchar(255) not null,	sel_expr varchar(255) not null,	sel_expr_u varchar(255),	from_tbls varchar(255) not null,	join_where varchar(255),	add_proc varchar(255),	delete_proc varchar(255),	param_order tinyint not null,	expect_return tinyint not null);
CREATE TABLE IF NOT EXISTS ldap_entries (	id integer unsigned not null primary key auto_increment,	dn varchar(255) not null,	oc_map_id integer unsigned not null DEFAULT '1' references ldap_oc_mappings(id),	parent int NOT NULL DEFAULT '1',	keyval BIGINT(20) NOT NULL );
alter table ldap_entries add 	constraint unq1_ldap_entries unique	(		oc_map_id,		keyval	);
alter table ldap_entries add	constraint unq2_ldap_entries unique	(		dn	);
CREATE TABLE IF NOT EXISTS `ldap_entry_objclasses` (	`entry_id` INT(10) UNSIGNED NOT NULL,	`oc_name` VARCHAR(64) NULL DEFAULT NULL COLLATE 'utf8mb4_general_ci',	INDEX `ldap_entry_objclasses_ibfk_1` (`entry_id`) USING BTREE,	CONSTRAINT `ldap_entry_objclasses_ibfk_1` FOREIGN KEY (`entry_id`) REFERENCES `ldap_entries` (`id`) ON UPDATE CASCADE ON DELETE CASCADE) COLLATE='utf8mb4_general_ci' ENGINE=InnoDB;
